"use strict";

const expects = require('chai').expect;
const CompileStrategy = require('../../lib/CompileStrategy/CompileStrategy');

describe('CompileStrategy', () =>
{
    let compileStrategy = null;

    beforeEach(() =>
    {
        compileStrategy = new CompileStrategy();
    });

    describe('#compile', () =>
    {
        it('throws a ReferenceError if this class is instantiated directly and not properly subclassed', () =>
        {
            expects(() => compileStrategy.compile('test', {})).to.throw(ReferenceError);
        });
    });

    describe('#getActiveEngine', () =>
    {
        it('returns the currently active engine instance', () =>
        {
            expects(() => compileStrategy.getActiveEngine()).to.throw(ReferenceError);
        });
    });
});
